INSERT INTO nukotan_quiz (question, type, answer1, answer2, answer3, answer4, level, comment) VALUES ('ねこまんまブログ開始年はいつでしょう？', 'one_answer', '2006年', '2007年', '2004年', '2005年', 'normal', '2006年5月31日20時57分に記念すべき最初の記事が投稿されました。
